const server_url="https://boarding-games-server.vercel.app";async function login(e){e.preventDefault();const t=document.getElementById("id_user").value.trim(),s=document.getElementById("user_password").value.trim(),o=await fetch(`${server_url}/api/auth/login`,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({username:t,password:s})});if(200!==o.status){const e=await o.json();return void alert(`Erreur : ${e.message||o.statusText}`)}const{sessionToken:r,userId:a,level_permission:n}=await o.json();localStorage.setItem("token",r),localStorage.setItem("id_user",a),localStorage.setItem("username",t),localStorage.setItem("level_permission",n),window.location.href="index.html"}