const server_url="https://boarding-games-server.vercel.app";async function signin(e){e.preventDefault(),console.log("eze");const t={username:document.getElementById("username").value.trim(),password:document.getElementById("password").value.trim()};try{const e=await fetch(`${server_url}/api/auth/signup`,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(t)});if(201===e.status){document.getElementById("formMessage").innerHTML="Création du compte réussi.";await e.json()}else document.getElementById("formMessage").innerHTML="<p><strong>Cet identifiant est déjà pris.</strong><p>"}catch(e){console.error(e),document.getElementById("formMessage").innerHTML="<p><strong>Impossible de contacter le serveur.</strong><p>"}return!1}